// Color Variables

// Header Variables
$nav-color: #54a5a3;
// Global Variables
$white: #fff;
$btn-color: #52c23f;

// :root {
//   --gradient: linear-gradient(90deg, $nav-color);
// }
* {
  font-family: 'Poppins', sans-serif;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  text-decoration: none;
  outline: none;
  transition: all 0.2s linear;
}

*::selection {
  background: $white;
  color: $nav-color;
}

html {
  font-size: 62.5%;
  overflow-x: hidden;
}

// Header

header {
  width: 100%;
  display: flex;
  justify-content: space-between;
  position: fixed;
  top: 0;
  left: 0;
  background: $white;
  z-index: 1000;
  padding: 2rem 7%;

  .logo {
    font-size: 2.5rem;
    color: $nav-color;
    z-index: 2;
  }
  .navbar ul {
    display: flex;
    align-items: center;
    justify-content: center;
    list-style: none;
  }
  .navbar ul li a {
    font-size: 2rem;
    margin-left: 2rem;
    color: $nav-color;
    position: relative;
  }
  .navbar ul li a::before {
    content: '';
    position: absolute;
    bottom: -0.5rem;
    right: 0;
    height: 0.2rem;
    width: 0%;
    background: $nav-color;
    transition: 0.2s linear;
  }

  .navbar ul li a.active:before,
  .navbar ul li a:hover:before {
    left: 0;
    width: 100%;
  }
  #menu {
    font-size: 3rem;
    color: $nav-color;
    cursor: pointer;
    display: none;
    z-index: 2;
  }
}

// Home Section
.home {
  background: url('../../imgs/hero.jpg');
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  background-repeat: no-repeat;
  background-size: cover;
  background-attachment: fixed;
  background-position: center center;
  min-height: 450px;
  // padding-top: 350px;

  .dark-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    min-height: 450px;
    background: rgba(0, 0, 0, 0.5);
  }
  .content {
    height: 450px;
    display: flex;
    text-align: center;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: $white;
    line-height: 1.3;
    h2 {
      font-size: 8rem;
    }
    p {
      font-size: 5.5rem;
    }
    .btn {
      padding: 1rem 3.5rem;
      font-size: 2rem;
      margin-top: 1.5rem;
      border: none;
      background: $btn-color;
      color: $white;
      cursor: pointer;
    }
    .btn:hover {
      background: $nav-color;
    }
  }
}

// Media Queries

// Header
@media (max-width: 768px) {
  html {
    font-size: 55%;
  }
  header #menu {
    display: block;
  }
  header .navbar {
    position: fixed;
    top: -120%;
    left: 0;
    width: 100%;
    background: $white;
  }
  header .navbar ul {
    flex-flow: column;
    padding: 2rem;
  }
  header .navbar ul li {
    width: 100%;
    text-align: center;
  }
  header .navbar ul li a {
    display: block;
    margin: 1rem 0;
    padding: 1rem;
    font-size: 2.5rem;
    z-index: 0;
  }
  header .navbar ul li a::before {
    height: 100%;
    bottom: 0;
    z-index: -1;
    background: $white;
  }
  header .navbar.nav-toggle {
    top: 7.4rem;
  }
}

// About

.about {
  background: #ededee;
  padding: 2.5rem 0;
  color: #333;
  .heading {
    text-align: center;
    font-size: 3rem;
    padding: 1rem;
  }
  .row {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    .image {
      flex: 0.3 1 45rem;
      padding: 3rem;
      text-align: center;
      img {
        max-width: 100%;
        border-radius: 0.5rem;
      }
    }
    .content {
      // flex: 1 1 45rem;
      // padding: 2.5rem;
      flex: 0.3 1 45rem;
      padding: 1rem 3rem;

      h3 {
        font-size: 2.5rem;
      }

      p {
        font-size: 1.8rem;
        padding: 1rem 0;

        i {
          padding-right: 0.5rem;
          color: $nav-color;
        }
      }
      .btn {
        padding: 1rem 2.5rem;
        font-size: 1.5rem;
        margin-top: 1.2rem;
        border: none;
        background: $nav-color;
        color: $white;
        cursor: pointer;
      }
      .btn:hover {
        background: $btn-color;
      }
    }
  }
}

// Services

.services {
  // min-height: 100vh;
  background: #ddd;
  color: #333;
  padding: 2.5rem 0;

  .heading {
    text-align: center;
    font-size: 3rem;
    padding: 1rem;
  }

  .box-container {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;

    .box {
      text-align: center;
      margin: 1.5rem;
      padding: 1rem;
      border: 0.2rem solid $nav-color;
      width: 27rem;
    }
    img {
      height: 8rem;
      width: 8rem;
      line-height: 7rem;
      text-align: center;
      margin: 1rem 0;
    }
    h3 {
      font-size: 2.5rem;
    }
    p {
      font-size: 1.5rem;
      padding: 1rem 0.8rem;
    }
  }
}

// Portfolio

.portfolio {
  background: #ededee;
  // min-height: 100vh;
  padding-top: 2.5rem;
  padding-bottom: 1rem;
  .heading {
    text-align: center;
    font-size: 3rem;
    padding: 1rem;
  }
  .gallery {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    width: 90%;
    margin: 0 auto;

    a {
      height: 200px;
      width: 300px;
      margin: 20px;
      border-radius: 5px;
      overflow: hidden;
      box-shadow: 0 3px 5px #333;

      img {
        height: 100%;
        width: 100%;
        object-fit: cover;
      }
      :hover {
        transform: scale(1.1);
      }
    }
  }
}

.contact {
  padding: 4.5rem 2.5rem 2.5rem 2.5rem;
  // min-height: 100vh;
  background: #ddd;
  color: #333;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;

  form {
    flex: 0.3 1 40rem;
    padding: 3rem;
    box-shadow: 0 0 1rem rgba(0, 0, 0, 0.3);
    border-radius: 5px;

    .box {
      height: 5rem;
      width: 100%;
      background: none;
      border: none;
      border-bottom: 0.1rem solid #000;
      font-size: 2rem;
      margin: 1rem 0;
      color: #333;
      text-decoration: none;
    }
    textarea {
      height: 15rem;
      width: 100%;
      background: none;
      border: none;
      border-bottom: 0.1rem solid #000;
      font-size: 2rem;
      margin: 1rem 0;
      color: #333;
      text-decoration: none;
      resize: none;
    }
    .btn {
      width: 100%;
      padding: 1rem 3.5rem;
      font-size: 2rem;
      margin-top: 1.5rem;
      border: none;
      background: $btn-color;
      color: $white;
      cursor: pointer;
    }
    .btn:hover {
      background: $nav-color;
    }
  }
  .content {
    flex: 0.3 1 40rem;
    padding: 4rem;

    h3 {
      font-size: 4rem;
    }
    p {
      font-size: 1.8rem;
      padding: 1rem 0;
    }

    .icons {
      display: flex;
      align-items: center;

      p {
        font-size: 2rem;
      }

      i {
        font-size: 2rem;
        color: $btn-color;
        padding-right: 1rem;
      }
    }
  }
}

// Footer
.footer {
  text-align: center;
  color: #333;
  background: #ededee;

  .share {
    padding: 1rem 0;

    h3 {
      font-size: 3rem;
    }

    a {
      font-size: 3rem;
      color: $nav-color;
      margin: 3rem 1.4rem;
    }
    a:hover {
      color: $btn-color;
    }
  }
  .content {
    padding: 1rem 2rem;
    background: $nav-color;
    color: #fff;
    text-align: center;
  }
}

@media (max-width: 500px) {
  html {
    font-size: 50%;
  }
  .home .content h1 {
    font-size: 8rem;
  }
  .home .content p {
    font-size: 5rem;
  }
}

@media (max-width: 899px) {
  .contact {
    display: flex;
    flex-direction: column-reverse;
  }
}
